function get_monologue(n_items){
    candidate_list = [];
    while(candidate_list.length < n_items){
	candidate_list = candidate_list.concat(shuffle(targ_lang == "eng" ? eng_monologue_library : zh_monologue_library)[0])
    }
    return(
	candidate_list.slice(0,n_items)
    )
}
